var HLW= require('hearthstone-log-watcher');
var watcher = new HLW();

console.log('is on?', watcher.on);

watcher.on('game-start', function(players){
	console.log("GAME STR");
	players.forEach(function(player){
	if(player.team === 'FRIENDLY'){
		console.log('friendly %s', player.name);
		}
	});
});

watcher.on('zone-change', function(data){
	console.log('%s moved to %s %s', data.cardName, data.team, data.zone);
});

watcher.on('game-over', function(players){
	console.log("GG");
	players.forEach(function(player){
		if(player.status === 'WON'){
			console.log('winner is %s', player.name);
		}
	});
});

watcher.start();